<html><body bgcolor="#99ffff"><?php if(isset($_POST['searchcheck'])){   $search=$_POST['search'];   include('dbconnect.php');  $sql="SELECT * FROM `blog` WHERE `title` LIKE '%$search%' OR `body` LIKE '%$search%'";  $result=mysql_query($sql) or die(mysql_error());   $num_rows=mysql_num_rows($result);      if($num_rows>0){  while($data=mysql_fetch_assoc($result)){          ?>	<div align="center">	<p><?php echo $data['title']; ?></p>	<p><?php $body=$data['body']; 	    echo substr($body,0,100);		?>		<a href="index.php?action=details_view_post&id=<?php echo $data['blogId'] ?>">Details</a></p>		<?php		 }				?>	</div>  <?php     }	  else{   header("LOCATION:index.php?action=create_post&msg=Search Not Found");    }  }	  ?></body></html>